mapper.select:
SELECT clt.CL_LotNo, clt.CL_ItemCode, pit.Product_Item_Name CL_ItemName, pit.Product_Info_STND_1 CL_STND_1, dt.Child_TBL_Type CL_Item_Clsfc_Name_1, dt2.Child_TBL_Type CL_Item_Clsfc_Name_2, dt3.Child_TBL_Type CL_Item_Material, eit.Equipment_Info_Name CL_EquipName, clt.CL_CrateCode, clt.CL_MachineCode, sum(clt.CL_Qty) CL_Qty, date(clt.CL_Create_Date) CL_Create_Date FROM Crate_Lot_tbl clt INNER JOIN Product_Info_tbl pit ON clt.CL_ItemCode = pit.Product_Item_Code INNER JOIN Equipment_Info_tbl eit ON clt.CL_MachineCode = eit.Equipment_Info_Code INNER JOIN DTL_TBL dt ON pit.PRODUCT_ITEM_CLSFC_1 = dt.CHILD_TBL_NO INNER JOIN DTL_TBL dt2 ON pit.PRODUCT_ITEM_CLSFC_2 = dt2.CHILD_TBL_NO INNER JOIN DTL_TBL dt3 ON eit.Equipment_Type = dt3.Child_TBL_No INNER JOIN DTL_TBL dt4 ON pit.Product_Material = dt4.Child_TBL_No
WHERE clt.CL_Create_Date >= #{startDate} AND clt.CL_Create_Date < #{endDate}
AND eit.Equipment_Info_Code LIKE CONCAT('%',#{MachineCode},'%') AND dt3.Child_TBL_No = '324' GROUP BY clt.CL_LotNo WITH ROLLUP

SELECT clt.CL_LotNo, clt.CL_ItemCode, pit.Product_Item_Name CL_ItemName, pit.Product_Info_STND_1 CL_STND_1, dt.Child_TBL_Type CL_Item_Clsfc_Name_1, dt2.Child_TBL_Type CL_Item_Clsfc_Name_2, dt3.Child_TBL_Type CL_Item_Material, eit.Equipment_Info_Name CL_EquipName, clt.CL_CrateCode, clt.CL_MachineCode2, sum(clt.CL_Qty) CL_Qty, date(clt.CL_Create_Date) CL_Create_Date FROM Crate_Lot_tbl clt INNER JOIN Product_Info_tbl pit ON clt.CL_ItemCode = pit.Product_Item_Code INNER JOIN Equipment_Info_tbl eit ON clt.CL_MachineCode2 = eit.Equipment_Info_Code INNER JOIN DTL_TBL dt ON pit.PRODUCT_ITEM_CLSFC_1 = dt.CHILD_TBL_NO INNER JOIN DTL_TBL dt2 ON pit.PRODUCT_ITEM_CLSFC_2 = dt2.CHILD_TBL_NO INNER JOIN DTL_TBL dt3 ON eit.Equipment_Type = dt3.Child_TBL_No INNER JOIN DTL_TBL dt4 ON pit.Product_Material = dt4.Child_TBL_No
WHERE clt.CL_Create_Date >= #{startDate} AND clt.CL_Create_Date < #{endDate}
AND eit.Equipment_Info_Code LIKE CONCAT('%',#{MachineCode},'%') AND dt3.Child_TBL_No = '325' GROUP BY clt.CL_LotNo WITH ROLLUP
