mapper.select:
SELECT somt.Sales_Order_mCus_No, somt.Sales_Order_mCode, sct.Cus_Name Sales_Order_mName, somt.Sales_Order_mDate, somt.Sales_Order_mTotal, somt.Sales_Order_mDlvry_Date, somt.Sales_Order_mRemarks, somt.Sales_Order_mModifier, somt.Sales_Order_mModify_Date, somt.Sales_Order_mCheck FROM Sales_OrderMaster_tbl somt INNER JOIN Customer_tbl sct ON somt.Sales_Order_mCode = sct.Cus_Code
WHERE somt.Sales_Order_mDate >= #{startDate} AND somt.Sales_Order_mDate < #{endDate}
AND Sales_Order_mCode like concat('%',#{ClientCode},'%')
AND NOT Sales_Order_mCheck = #{condition}
ORDER BY Sales_Order_mDate DESC
