mapper.select:
select count(*)+1 from Sales_OutMat_tbl
where Sales_OutMat_Cus_No = #{Sales_OutMat_Cus_No} 
	and Sales_OutMat_Lot_No = #{Sales_OutMat_Lot_No}

select somt.Sales_OutMat_Lot_No, somt.Sales_OutMat_Date, dt.Child_TBL_Type Sales_OutMat_Send_Clsfc, somt.Sales_OutMat_Client_Code, ct.Cus_Name Sales_OutMat_Client_Name, somt.Sales_OutMat_Code, pit.Product_Item_Name Sales_OutMat_Name, pit.Product_Info_STND_1 Sales_OutMat_STND_1, dt3.Child_TBL_Type Sales_OutMat_Item_Clsfc_Name_1, dt2.Child_TBL_Type Sales_OutMat_UNIT, somt.Sales_OutMat_Qty, somt.Sales_OutMat_Cus_No, somt.Sales_OutMat_Modifier, somt.Sales_OutMat_dInsert_Time from Sales_OutMat_tbl somt inner join DTL_TBL dt on somt.Sales_OutMat_Send_Clsfc = dt.CHILD_TBL_NO inner join Customer_tbl ct on somt.Sales_OutMat_Client_Code = ct.Cus_Code inner join Product_Info_tbl pit on somt.Sales_OutMat_Code = pit.Product_Item_Code inner join DTL_TBL dt2 on pit.Product_Unit = dt2.CHILD_TBL_NO inner join DTL_TBL dt3 ON pit.Product_Item_CLSFC_1 = dt3.Child_TBL_No
where somt.Sales_OutMat_Date >= #{startDate} and somt.Sales_OutMat_Date < #{endDate}
and somt.Sales_OutMat_Code like concat('%',#{Sales_OutMat_Code},'%')
and somt.Sales_OutMat_Client_Code like concat('%',#{Sales_OutMat_Client_Code},'%')
and somt.Sales_OutMat_Send_Clsfc = #{Sales_OutMat_Send_Clsfc}
and somt.Sales_OutMat_Lot_No like concat('%',#{Sales_OutMat_Lot_No},'%')

select somt.Sales_OutMat_Lot_No, somt.Sales_OutMat_Date, dt.Child_TBL_Type Sales_OutMat_Send_Clsfc, somt.Sales_OutMat_Client_Code, ct.Cus_Name Sales_OutMat_Client_Name, somt.Sales_OutMat_Code, pit.Product_Item_Name Sales_OutMat_Name, pit.Product_Info_STND_1 Sales_OutMat_STND_1, dt2.Child_TBL_Type Sales_OutMat_UNIT, dt3.Child_TBL_Type Sales_OutMat_Item_Clsfc_Name_1, sum(somt.Sales_OutMat_Qty) Sales_OutMat_Qty from Sales_OutMat_tbl somt inner join DTL_TBL dt on somt.Sales_OutMat_Send_Clsfc = dt.Child_TBL_No inner join Customer_tbl ct on somt.Sales_OutMat_Client_Code = ct.Cus_Code inner join Product_Info_tbl pit on somt.Sales_OutMat_Code = pit.Product_Item_Code inner join DTL_TBL dt2 on pit.Product_Unit = dt2.Child_TBL_No inner join DTL_TBL dt3 ON pit.Product_Item_CLSFC_1 = dt3.Child_TBL_No
where somt.Sales_OutMat_Date >= #{startDate} and somt.Sales_OutMat_Date < #{endDate}
and somt.Sales_OutMat_Code like concat('%',#{Sales_OutMat_Code},'%')
and somt.Sales_OutMat_Send_Clsfc = #{Sales_OutMat_Send_Clsfc}
group by somt.Sales_OutMat_Code, somt.Sales_OutMat_Date with ROLLUP

select somt.Sales_OutMat_Lot_No, somt.Sales_OutMat_Date, dt.CHILD_TBL_TYPE Sales_OutMat_Send_Clsfc, somt.Sales_OutMat_Client_Code, ct.Cus_Name Sales_OutMat_Client_Name, somt.Sales_OutMat_Code, pit.PRODUCT_ITEM_NAME Sales_OutMat_Name, pit.PRODUCT_INFO_STND_1 Sales_OutMat_STND_1, dt2.CHILD_TBL_TYPE Sales_OutMat_UNIT, sum(somt.Sales_OutMat_Qty) Sales_OutMat_Qty, sum(somt.Sales_OutMat_Price) Sales_OutMat_Price from Sales_OutMat_tbl somt inner join DTL_TBL dt on somt.Sales_OutMat_Send_Clsfc = dt.CHILD_TBL_NO inner join Customer_tbl ct on somt.Sales_OutMat_Client_Code = ct.Cus_Code inner join Product_Info_tbl pit on somt.Sales_OutMat_Code = pit.PRODUCT_ITEM_CODE inner join DTL_TBL dt2 on pit.PRODUCT_UNIT = dt2.CHILD_TBL_NO
where somt.Sales_OutMat_Date >= #{startDate} and somt.Sales_OutMat_Date < #{endDate}
and Sales_OutMat_Client_Code LIKE CONCAT('%',#{ClientCode},'%')
and Sales_OutMat_Send_Clsfc = #{ItemSendClsfc}
group by somt.Sales_OutMat_Client_Code, somt.Sales_OutMat_Code with rollup

select somt.Sales_OutMat_No, somt.Sales_OutMat_Client_Code, ct.Cus_Name Sales_OutMat_Client_Name, sum(somt.Sales_OutMat_Qty) Sales_OutMat_Qty from Sales_OutMat_tbl somt inner join Customer_tbl ct on somt.Sales_OutMat_Client_Code = ct.Cus_Code
where somt.Sales_OutMat_Date >= #{startDate} and somt.Sales_OutMat_Date < #{endDate}
group by Sales_OutMat_Client_Code

mapper.insert:
insert into Sales_OutMat_tbl( Sales_OutMat_No, Sales_OutMat_Cus_No, Sales_OutMat_Lot_No, Sales_OutMat_Code, Sales_OutMat_Qty, Sales_OutMat_Unit_Price, Sales_OutMat_Price, Sales_OutMat_Client_Code, Sales_OutMat_Send_Clsfc, Sales_OutMat_Date, Sales_OutMat_dInsert_Time, Sales_OutMat_Modifier
) values (
(SELECT ifnull(max(AA.Sales_OutMat_No)+1,1) FROM Sales_OutMat_tbl AA WHERE AA.Sales_OutMat_Cus_No = #{Sales_OutMat_Cus_No}), #{Sales_OutMat_Cus_No}, #{Sales_OutMat_Lot_No}, #{Sales_OutMat_Code}, #{Sales_OutMat_Qty}, #{Sales_OutMat_Unit_Price}, #{Sales_OutMat_Qty} * #{Sales_OutMat_Unit_Price}, #{Sales_OutMat_Client_Code}, #{Sales_OutMat_Send_Clsfc}, #{Sales_OutMat_Date}, now(), #{Sales_OutMat_Modifier}
)
