mapper.insert:
insert into Crate_tbl(
	C_CrateCode, C_Condition,
    C_Create_Date, C_Use_Status, C_Production_LotNo
)values(
	#{C_CrateCode}, #{C_Condition},
    now(), #{C_Use_Status}, #{c_Production_LotNo}
) on duplicate key update
C_Condition = #{C_Condition},
C_Create_Date = now(),
C_Use_Status = #{C_Use_Status},
C_Production_LotNo = #{C_Production_LotNo}

mapper.update:
update Crate_tbl
set

C_Production_LotNo = #{C_Production_LotNo},

C_Condition = #{C_Condition}
where C_CrateCode = #{C_CrateCode}
