mapper.insert:
insert into Crate_tbl(
	C_CrateCode, C_Condition,
    C_Create_Date, C_Use_Status
)values(
	#{C_CrateCode}, 1,
    now(), #{C_Use_Status}
) on duplicate key update
	C_Use_Status = #{C_Use_Status}

mapper.update:
update Crate_tbl
set
C_Use_Status = #{C_Use_Status}
where C_CrateCode = #{C_CrateCode}
